import React, { useState, useRef } from 'react';
import './Graph.css';

function Graph({ points = [], r = 1, onPointClick }) {
    const svgRef = useRef(null);
    const [hoverCoords, setHoverCoords] = useState(null);

    // Конвертация экранных координат в системные
    const screenToSystem = (screenX, screenY) => {
        const svg = svgRef.current;
        if (!svg) return { x: 0, y: 0 };

        const rect = svg.getBoundingClientRect();

        // Координаты относительно центра SVG (150, 150)
        const relativeX = screenX - rect.left - 150;
        const relativeY = screenY - rect.top - 150;

        // Масштаб: 100 пикселей = R единиц
        const scale = 100 / Math.abs(r);

        // Конвертация
        const systemX = relativeX / scale;
        const systemY = -relativeY / scale; // Инвертируем Y

        return { x: systemX, y: systemY };
    };

    // Конвертация системных координат в экранные
    const systemToScreen = (systemX, systemY) => {
        const scale = 100 / Math.abs(r);
        const screenX = 150 + systemX * scale;
        const screenY = 150 - systemY * scale; // Инвертируем Y

        return { x: screenX, y: screenY };
    };

    // Обработка клика по графику
    const handleClick = (event) => {
        if (!onPointClick) return;

        const { x, y } = screenToSystem(event.clientX, event.clientY);
        onPointClick(x, y);
    };

    // Обработка движения мыши
    const handleMouseMove = (event) => {
        const { x, y } = screenToSystem(event.clientX, event.clientY);
        setHoverCoords({
            x: x.toFixed(2),
            y: y.toFixed(2)
        });
    };

    const renderGrid = () => {
        const lines = [];
        const cellSize = 20; // 1 клетка = 20px = 1 единица

        // Вертикальные линии (-5...+5)
        for (let i = -5; i <= 5; i++) {
            const x = 150 + i * cellSize;
            lines.push(
                <line key={`grid_v_${i}`} x1={x} y1="50" x2={x} y2="250" stroke="#e8e8e8" strokeWidth="1"/>
            );
        }

        // Горизонтальные линии (-5...+5)
        for (let i = -5; i <= 5; i++) {
            const y = 150 - i * cellSize;
            lines.push(
                <line key={`grid_h_${i}`} x1="50" y1={y} x2="250" y2={y} stroke="#e8e8e8" strokeWidth="1"/>
            );
        }
        return lines;
    };

    const renderAxes = () => {
        const cellSize = 20;

        return (
            <>
                {/* Оси */}
                <line x1="50" y1="150" x2="250" y2="150" stroke="black" strokeWidth="2"/>
                <line x1="150" y1="50" x2="150" y2="250" stroke="black" strokeWidth="2"/>

                {/* Деления и подписи -5 до +5 */}
                {[-5, -4, -3, -2, -1, 1, 2, 3, 4, 5].map(i => {
                    const xPos = 150 + i * cellSize;
                    const yPos = 150 - i * cellSize;

                    return (
                        <g key={`tick_${i}`}>
                            <line x1={xPos} y1="148" x2={xPos} y2="152" stroke="black"/>
                            <text x={xPos} y="170" textAnchor="middle" fontSize="11">{i}</text>
                            <line x1="148" y1={yPos} x2="152" y2={yPos} stroke="black"/>
                            <text x="140" y={yPos + 3} textAnchor="end" fontSize="11">{i}</text>
                        </g>
                    );
                })}
            </>
        );
    };

    const renderArea = () => {
        if (r <= 0) return null;

        return (
            <>
                {/* 1. Треугольник: высота R клеток */}
                <polygon points={`150,${150 - r * 20} ${150 - r * 20},150 150,150`}
                         fill="rgba(100, 149, 237, 0.3)" stroke="#6495ED" strokeWidth="1.5"/>

                {/* 2. Прямоугольник: ширина R/2, высота R */}
                <rect x={150 - (r/2) * 20} y="150" width={(r/2) * 20} height={r * 20}
                      fill="rgba(144, 238, 144, 0.3)" stroke="#90EE90" strokeWidth="1.5"/>

                {/* 3. Сектор: радиус R */}
                <path d={`M 150 150 L ${150 + r * 20} 150 A ${r * 20} ${r * 20} 0 0 1 150 ${150 + r * 20} Z`}
                      fill="rgba(255, 182, 193, 0.3)" stroke="#FFB6C1" strokeWidth="1.5"/>
            </>
        );
    };


    // Отрисовка точек
    const renderPoints = () => {
        return points.map((point, index) => {
            const { x: screenX, y: screenY } = systemToScreen(point.x, point.y);

            return (
                <circle
                    key={index}
                    cx={screenX}
                    cy={screenY}
                    r="3"
                    fill={point.hit ? "#4CAF50" : "#F44336"} // Зеленый/красный
                    stroke={point.hit ? "#2E7D32" : "#C62828"}
                    strokeWidth="1"
                >
                    <title>
                        Точка ({point.x.toFixed(2)}, {point.y.toFixed(2)}),
                        R = {point.r},
                        {point.hit ? ' попала' : ' не попала'}
                    </title>
                </circle>
            );
        });
    };

    return (
        <div className="graph-container">
            <h3>График области (R = {r})</h3>

            {/* Отображение координат при наведении */}
            {hoverCoords && (
                <div className="coordinates-hint">
                    Координаты: X = {hoverCoords.x}, Y = {hoverCoords.y}
                </div>
            )}

            {/* SVG график */}
            <svg
                ref={svgRef}
                width="300"
                height="300"
                viewBox="0 0 300 300"
                onClick={handleClick}
                onMouseMove={handleMouseMove}
                onMouseLeave={() => setHoverCoords(null)}
                className="graph-svg"
            >
                {/* Сетка */}
                {renderGrid()}

                {/* Область */}
                {renderArea()}

                {/* Оси */}
                {renderAxes()}

                {/* Точки */}
                {renderPoints()}

            </svg>

            {/* Легенда */}
            <div className="legend">
                <div className="legend-item">
                    <div className="legend-color hit"></div>
                    <span>Точка попала в область</span>
                </div>
                <div className="legend-item">
                    <div className="legend-color miss"></div>
                    <span>Точка не попала в область</span>
                </div>
            </div>
        </div>
    );
}

export default Graph;
