import React, { useState, useRef } from 'react';
import './Graph.css';

function Graph({ points = [], r = 1, onPointClick }) {
    const svgRef = useRef(null);
    const [hoverCoords, setHoverCoords] = useState(null);

    // Конвертация экранных координат в системные
    const screenToSystem = (screenX, screenY) => {
        const svg = svgRef.current;
        if (!svg) return { x: 0, y: 0 };

        const rect = svg.getBoundingClientRect();

        // Координаты относительно центра SVG (150, 150)
        const relativeX = screenX - rect.left - 150;
        const relativeY = screenY - rect.top - 150;

        // Масштаб: 100 пикселей = R единиц
        const scale = 100 / Math.abs(r);

        // Конвертация
        const systemX = relativeX / scale;
        const systemY = -relativeY / scale; // Инвертируем Y

        return { x: systemX, y: systemY };
    };

    // Конвертация системных координат в экранные
    const systemToScreen = (systemX, systemY) => {
        const scale = 100 / Math.abs(r);
        const screenX = 150 + systemX * scale;
        const screenY = 150 - systemY * scale; // Инвертируем Y

        return { x: screenX, y: screenY };
    };

    // Обработка клика по графику
    const handleClick = (event) => {
        if (!onPointClick) return;

        const { x, y } = screenToSystem(event.clientX, event.clientY);
        onPointClick(x, y);
    };

    // Обработка движения мыши
    const handleMouseMove = (event) => {
        const { x, y } = screenToSystem(event.clientX, event.clientY);
        setHoverCoords({
            x: x.toFixed(2),
            y: y.toFixed(2)
        });
    };

    // Отрисовка области (по вашему описанию)
    const renderArea = () => {
        if (r <= 0) return null;

        const absR = Math.abs(r);

        return (
            <>
                {/* 1. Треугольник (верхний левый квадрант) */}
                <polygon
                    points={`150,${150 - absR * 20} ${150 - absR * 20},150 150,150`}
                    fill="rgba(100, 149, 237, 0.5)" // cornflowerblue
                    stroke="#6495ED"
                    strokeWidth="1"
                />

                {/* 2. Прямоугольник (нижний левый квадрант) */}
                <rect
                    x={150 - absR * 10} // -R/2
                    y={150}
                    width={absR * 10}   // R/2
                    height={absR * 20}  // R
                    fill="rgba(144, 238, 144, 0.5)" // lightgreen
                    stroke="#90EE90"
                    strokeWidth="1"
                />

                {/* 3. Сектор окружности (нижний правый квадрант) */}
                <path
                    d={`M 150 150 
                        L ${150 + absR * 20} 150 
                        A ${absR * 20} ${absR * 20} 0 0 0 150 ${150 + absR * 20} 
                        Z`}
                    fill="rgba(255, 182, 193, 0.5)" // lightpink
                    stroke="#FFB6C1"
                    strokeWidth="1"
                />
            </>
        );
    };

    // Отрисовка координатной сетки
    const renderGrid = () => {
        const lines = [];
        const step = 20; // 20 пикселей = 0.2R (при R=1)

        // Вертикальные линии
        for (let x = 50; x <= 250; x += step) {
            if (x === 150) continue; // Пропускаем ось Y
            lines.push(
                <line
                    key={`v${x}`}
                    x1={x}
                    y1="50"
                    x2={x}
                    y2="250"
                    stroke="#e0e0e0"
                    strokeWidth="1"
                />
            );
        }

        // Горизонтальные линии
        for (let y = 50; y <= 250; y += step) {
            if (y === 150) continue; // Пропускаем ось X
            lines.push(
                <line
                    key={`h${y}`}
                    x1="50"
                    y1={y}
                    x2="250"
                    y2={y}
                    stroke="#e0e0e0"
                    strokeWidth="1"
                />
            );
        }

        return lines;
    };

    // Отрисовка осей координат
    const renderAxes = () => (
        <>
            {/* Ось X */}
            <line
                x1="50"
                y1="150"
                x2="250"
                y2="150"
                stroke="black"
                strokeWidth="2"
            />

            {/* Ось Y */}
            <line
                x1="150"
                y1="50"
                x2="150"
                y2="250"
                stroke="black"
                strokeWidth="2"
            />

            {/* Стрелки */}
            <polygon points="250,150 245,145 245,155" fill="black" />
            <polygon points="150,50 145,55 155,55" fill="black" />

            {/* Подписи */}
            <text x="255" y="155" fontSize="12">X</text>
            <text x="145" y="45" fontSize="12">Y</text>

            {/* Деления и подписи */}
            {[-2, -1, 1, 2].map(i => {
                const xPos = 150 + i * 50 * (r / 2);
                const yPos = 150 - i * 50 * (r / 2);
                const value = (i * r / 2).toFixed(1);

                return (
                    <g key={`ticks${i}`}>
                        {/* Деления на оси X */}
                        <line x1={xPos} y1="145" x2={xPos} y2="155" stroke="black" />
                        <text x={xPos} y="170" textAnchor="middle" fontSize="10">{value}</text>

                        {/* Деления на оси Y */}
                        <line x1="145" y1={yPos} x2="155" y2={yPos} stroke="black" />
                        <text x="130" y={yPos + 4} textAnchor="end" fontSize="10">{value}</text>
                    </g>
                );
            })}
        </>
    );

    // Отрисовка точек
    const renderPoints = () => {
        return points.map((point, index) => {
            const { x: screenX, y: screenY } = systemToScreen(point.x, point.y);

            return (
                <circle
                    key={index}
                    cx={screenX}
                    cy={screenY}
                    r="3"
                    fill={point.hit ? "#4CAF50" : "#F44336"} // Зеленый/красный
                    stroke={point.hit ? "#2E7D32" : "#C62828"}
                    strokeWidth="1"
                >
                    <title>
                        Точка ({point.x.toFixed(2)}, {point.y.toFixed(2)}),
                        R = {point.r},
                        {point.hit ? ' попала' : ' не попала'}
                    </title>
                </circle>
            );
        });
    };

    return (
        <div className="graph-container">
            <h3>График области (R = {r})</h3>

            {/* Отображение координат при наведении */}
            {hoverCoords && (
                <div className="coordinates-hint">
                    Координаты: X = {hoverCoords.x}, Y = {hoverCoords.y}
                </div>
            )}

            {/* SVG график */}
            <svg
                ref={svgRef}
                width="300"
                height="300"
                viewBox="0 0 300 300"
                onClick={handleClick}
                onMouseMove={handleMouseMove}
                onMouseLeave={() => setHoverCoords(null)}
                className="graph-svg"
            >
                {/* Сетка */}
                {renderGrid()}

                {/* Область */}
                {renderArea()}

                {/* Оси */}
                {renderAxes()}

                {/* Точки */}
                {renderPoints()}

            </svg>

            {/* Легенда */}
            <div className="legend">
                <div className="legend-item">
                    <div className="legend-color hit"></div>
                    <span>Точка попала в область</span>
                </div>
                <div className="legend-item">
                    <div className="legend-color miss"></div>
                    <span>Точка не попала в область</span>
                </div>
            </div>
        </div>
    );
}

export default Graph;
