import React, { useState, useRef } from 'react';
import './Graph.css';

function Graph({ points = [], r = 1, onPointClick }) {
    const svgRef = useRef(null);
    const [hoverCoords, setHoverCoords] = useState(null);

    // Преобразование системных координат (-5..5) в экранные (0..300)
    const systemToScreen = (systemX, systemY) => {
        const scale = 30; // 300px / 10 единиц = 30px/единица
        const screenX = 150 + systemX * scale;
        const screenY = 150 - systemY * scale;
        return { x: screenX, y: screenY };
    };

    // Обработка клика - вся SVG область кликабельна!
    const handleClick = (event) => {
        if (!onPointClick) return;

        const svg = svgRef.current;
        if (!svg) return;

        const rect = svg.getBoundingClientRect();
        const svgX = event.clientX - rect.left;
        const svgY = event.clientY - rect.top;

        // Преобразование экранных координат в системные
        const systemX = (svgX - 150) / 30;
        const systemY = (150 - svgY) / 30;

        onPointClick(systemX, systemY);
    };

    // Hover координаты
    const handleMouseMove = (event) => {
        const svg = svgRef.current;
        if (!svg) return;

        const rect = svg.getBoundingClientRect();
        const svgX = event.clientX - rect.left;
        const svgY = event.clientY - rect.top;

        const systemX = (svgX - 150) / 30;
        const systemY = (150 - svgY) / 30;
        setHoverCoords({ x: systemX.toFixed(2), y: systemY.toFixed(2) });
    };

    // Сетка на всю площадь 0-300
    const renderGrid = () => {
        const lines = [];
        const cellSize = 30; // 300px / 10 = 30px на единицу

        // Вертикальные линии -5..+5
        for (let i = -5; i <= 5; i++) {
            const x = 150 + i * cellSize;
            lines.push(
                <line key={`grid_v_${i}`} x1={x} y1="0" x2={x} y2="300" stroke="#e8e8e8" strokeWidth="1"/>
            );
        }

        // Горизонтальные линии -5..+5
        for (let i = -5; i <= 5; i++) {
            const y = 150 - i * cellSize;
            lines.push(
                <line key={`grid_h_${i}`} x1="0" y1={y} x2="300" y2={y} stroke="#e8e8e8" strokeWidth="1"/>
            );
        }
        return lines;
    };

    // Оси через весь SVG
    const renderAxes = () => {
        const cellSize = 30;

        return (
            <>
                {/* Основные оси */}
                <line x1="0" y1="150" x2="300" y2="150" stroke="black" strokeWidth="2"/>
                <line x1="150" y1="0" x2="150" y2="300" stroke="black" strokeWidth="2"/>

                {/* Деления и подписи */}
                {[-5, -4, -3, -2, -1, 1, 2, 3, 4, 5].map(i => {
                    const xPos = 150 + i * cellSize;
                    const yPos = 150 - i * cellSize;
                    return (
                        <g key={`tick_${i}`}>
                            <line x1={xPos} y1="148" x2={xPos} y2="152" stroke="black"/>
                            <text x={xPos} y="170" textAnchor="middle" fontSize="11">{i}</text>
                            <line x1="148" y1={yPos} x2="152" y2={yPos} stroke="black"/>
                            <text x="135" y={yPos + 4} textAnchor="end" fontSize="11">{i}</text>
                        </g>
                    );
                })}
            </>
        );
    };

    // Область попадания (масштабируется с R)
    const renderArea = () => {
        if (r <= 0) return null;
        const scale = 30;

        return (
            <>
                {/* 1. Треугольник (1-я четверть) */}
                <polygon
                    points={`150,${150 - r * scale} ${150 - r * scale},150 150,150`}
                    fill="rgba(100, 149, 237, 0.3)"
                    stroke="#6495ED"
                    strokeWidth="1.5"
                />

                {/* 2. Прямоугольник (4-я четверть) */}
                <rect
                    x={150 - (r/2) * scale}
                    y="150"
                    width={(r/2) * scale}
                    height={r * scale}
                    fill="rgba(144, 238, 144, 0.3)"
                    stroke="#90EE90"
                    strokeWidth="1.5"
                />

                {/* 3. Сектор (2-я четверть) */}
                <path
                    d={`M 150 150 L ${150 + r * scale} 150 A ${r * scale} ${r * scale} 0 0 1 150 ${150 - r * scale} Z`}
                    fill="rgba(255, 182, 193, 0.3)"
                    stroke="#FFB6C1"
                    strokeWidth="1.5"
                />
            </>
        );
    };

    // Отрисовка точек
    const renderPoints = () => {
        return points.map((point, index) => {
            const { x: screenX, y: screenY } = systemToScreen(point.x, point.y);

            return (
                <circle
                    key={index}
                    cx={screenX}
                    cy={screenY}
                    r="4"
                    fill={point.hit ? "#4CAF50" : "#F44336"}
                    stroke={point.hit ? "#2E7D32" : "#C62828"}
                    strokeWidth="1.5"
                >
                    <title>
                        Точка ({point.x.toFixed(2)}, {point.y.toFixed(2)}),<br/>
                        R = {point.r.toFixed(1)},<br/>
                        {point.hit ? '✓ Попала' : '✗ Не попала'}
                    </title>
                </circle>
            );
        });
    };

    return (
        <div className="graph-container">
            <h3>График области (R = {r.toFixed(1)})</h3>

            {/* Координаты при наведении */}
            {hoverCoords && (
                <div className="coordinates-hint">
                    X = {hoverCoords.x} | Y = {hoverCoords.y}
                </div>
            )}

            {/* SVG - вся площадь кликабельна! */}
            <svg
                ref={svgRef}
                width="300"
                height="300"
                viewBox="0 0 300 300"
                onClick={handleClick}
                onMouseMove={handleMouseMove}
                onMouseLeave={() => setHoverCoords(null)}
                className="graph-svg"
            >
                {/* Сетка */}
                {renderGrid()}

                {/* Область */}
                {renderArea()}

                {/* Оси */}
                {renderAxes()}

                {/* Точки */}
                {renderPoints()}
            </svg>

            {/* Легенда */}
            <div className="legend">
                <div className="legend-item">
                    <div className="legend-color hit"></div>
                    <span>Попала в область</span>
                </div>
                <div className="legend-item">
                    <div className="legend-color miss"></div>
                    <span>Не попала</span>
                </div>
            </div>
        </div>
    );
}

export default Graph;
